import * as React from 'react';
import { StatusBar } from "react-native";
import { TouchableOpacity, Image, View, Text, TextInput } from 'react-native';
import { useFocusEffect } from "@react-navigation/native";
import * as ImagePicker from 'expo-image-picker';
import Constants from 'expo-constants';
import * as Permissions from 'expo-permissions';

import styles from './styles';

export default class Create extends React.Component {
  state = {
    image: null,
  };

  render() {
    let { image } = this.state;
    return (
      <View style={styles.container}>
        <StatusBar barStyle={'dark-content'} backgroundColor="red" />
        <Text style={styles.title}>Crie sua postagem</Text>
          {image &&
          <Image source={{ uri: image }} style={styles.preview} />}
        <TextInput placeholder="Descrição da postagem"
        editable
        multiline
        maxLength={200}
        style={styles.input} />
        <View style={styles.buttons}>
        <TouchableOpacity
          onPress={this._pickImage}
          style={styles.select}
        >
          <Text style={styles.selectText}>Selecione uma imagem</Text>
        </TouchableOpacity>
        <TouchableOpacity title="Criar" onPress={() => alert('Criado')} style={styles.submit} >
          <Text style={styles.submitText}>Criar</Text>
        </TouchableOpacity>
        </View>
      </View>
    );
  }

  componentDidMount() {
    this.getPermissionAsync();
    console.log('hi');
  }

  getPermissionAsync = async () => {
    if (Constants.platform.ios) {
      const { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
      if (status !== 'granted') {
        alert('Sorry, we need camera roll permissions to make this work!');
      }
    }
  }

  _pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1
    });

    console.log(result);

    if (!result.cancelled) {
      this.setState({ image: result.uri });
    }
  };

}
